<body><p style='color: rgba(0, 0, 0, 1.000000); font: 16px "Helvetica Neue"; -cocoa-font-postscriptname: "HelveticaNeue"; '>@mixin gen-col($n) {</p><p style='color: rgba(0, 0, 0, 1.000000); font: 16px "Helvetica Neue"; -cocoa-font-postscriptname: "HelveticaNeue"; '>  @if $n &gt; 0 {</p><p style='color: rgba(0, 0, 0, 1.000000); font: 16px "Helvetica Neue"; -cocoa-font-postscriptname: "HelveticaNeue"; '>    @include gen-col($n -1);</p><p style='color: rgba(0, 0, 0, 1.000000); font: 16px "Helvetica Neue"; -cocoa-font-postscriptname: "HelveticaNeue"; '>    .col-#{$n} {</p><p style='color: rgba(0, 0, 0, 1.000000); font: 16px "Helvetica Neue"; -cocoa-font-postscriptname: "HelveticaNeue"; '>      Width: 1000px/12*$n;</p><p style='color: rgba(0, 0, 0, 1.000000); font: 16px "Helvetica Neue"; -cocoa-font-postscriptname: "HelveticaNeue"; '>    }</p><p style='color: rgba(0, 0, 0, 1.000000); font: 16px "Helvetica Neue"; -cocoa-font-postscriptname: "HelveticaNeue"; '>  }</p><p style='color: rgba(0, 0, 0, 1.000000); font: 16px "Helvetica Neue"; -cocoa-font-postscriptname: "HelveticaNeue"; '>}</p><p></p><p style='color: rgba(0, 0, 0, 1.000000); font: 16px "Helvetica Neue"; -cocoa-font-postscriptname: "HelveticaNeue"; '>@include gen-col(12);</p><p></p><p></p><p style='color: rgba(0, 0, 0, 1.000000); font: 16px "Helvetica Neue"; -cocoa-font-postscriptname: "HelveticaNeue"; '>@for $I from 1 through 12 {</p><p style='color: rgba(0, 0, 0, 1.000000); font: 16px "Helvetica Neue"; -cocoa-font-postscriptname: "HelveticaNeue"; '>  .col-#{$i} {</p><p style='color: rgba(0, 0, 0, 1.000000); font: 16px "Helvetica Neue"; -cocoa-font-postscriptname: "HelveticaNeue"; '>    width: 1000px/12*I;</p><p style='color: rgba(0, 0, 0, 1.000000); font: 16px "Helvetica Neue"; -cocoa-font-postscriptname: "HelveticaNeue"; '>  }</p><p style='color: rgba(0, 0, 0, 1.000000); font: 16px "Helvetica Neue"; -cocoa-font-postscriptname: "HelveticaNeue"; '>}</p></body>
</html>
